const rawIA = $('AI_Analizador_Llama').item.json.response;
// Si el ID viene del nodo anterior, √∫salo. Si no, intenta sacarlo del item actual
const idOriginal = $('DB_Upsert_Noticia_Cruda').item.json.id || items[0].json.id; 

let resultado = {};

try {
  // Regex para capturar el JSON incluso si la IA habla antes o despu√©s
  const jsonMatch = rawIA.match(/\{[\s\S]*\}/);
  resultado = jsonMatch ? JSON.parse(jsonMatch[0]) : {};
} catch (error) {
  // Si falla, objeto vac√≠o para que los defaults de abajo act√∫en
  resultado = {};
}

// --- L√ìGICA DE TAGS (Ya funcionando) ---
const rawCategorias = resultado.categorias || resultado.tags || [];
const rawConceptos = resultado.conceptos_clave || []; 
const rawEntidades = (resultado.entidades || []).map(e => e.nombre);

const tagsCombinados = [
    ...rawCategorias,
    ...rawConceptos,
    ...rawEntidades
];

const tagsFinales = [...new Set(
    tagsCombinados.map(t => t ? t.toString().toLowerCase().trim() : "")
)].filter(t => t.length > 2); 

// --- L√ìGICA DE BLINDAJE PARA SESGO Y TONO ---
// Buscamos variaciones comunes o asignamos default
const sesgo = resultado.sesgo_ia || resultado.Sesgo_ia || resultado.sesgo || "Indefinido";
const tono = resultado.tono_ia || resultado.Tono_ia || resultado.tono || "Informativo";

return {
  json: {
    id: idOriginal,
    titulo_limpio: resultado.titulo_neutro || $('DB_Upsert_Noticia_Cruda').item.json.titulo,

    resumen_ia: resultado.resumen_ia || "Sin resumen disponible.",

    // AQU√ç EST√Å EL ARREGLO: Usamos las variables con default
