const cheerio = require('cheerio');

const results = [];

for (const item of items) {
  // 1. Validamos que exista data
  if (!item.json.data) continue;

  const html = item.json.data;
  const $ = cheerio.load(html);

  // 2. FILTRO: Ignorar p√°ginas que no son art√≠culos
  const esArticulo = $('h1.entry-title').length > 0;

  if (!esArticulo) {
    continue; 
  }

  // 3. LIMPIEZA
  $('.heateor_sss_sharing_container').remove(); 
  $('.heateorSssClear').remove();
  $('script').remove();
  $('style').remove();
  $('.sharedaddy').remove();

  // 4. EXTRACCI√ìN
  const titulo = $('h1.entry-title').text().trim();

  const cuerpo = $('.entry-content p')
    .map((i, el) => $(el).text().trim())
    .get()
    .join('\n\n');

  const fecha = $('meta[property="article:published_time"]').attr('content');
  const autor = $('.by-author a').text().trim() || 'El Siglo';
  const link = $('link[rel="canonical"]').attr('href');

  // üî• L√ìGICA DE FOTOS MEJORADA (Basada en tu inspecci√≥n)
  let imagen = null;

  // Prioridad 1: Buscar im√°genes con la clase "wp-image" (son las fotos reales subidas)
  // Buscamos dentro de .entry-content para asegurarnos que es parte de la noticia
  const imgWp = $('.entry-content img[class*="wp-image-"]').first().attr('src');
  if (imgWp) {
      imagen = imgWp;
  }

  // Prioridad 2: Si no encuentra wp-image, buscar cualquier imagen dentro del contenido
  if (!imagen) {
      const imgGenerica = $('.entry-content img').first().attr('src');
      if (imgGenerica) imagen = imgGenerica;
  }

  // Prioridad 3: Imagen destacada (header)
  if (!imagen) {
      const imgHeader = $('.post-thumbnail img').attr('src');
      if (imgHeader) imagen = imgHeader;
  }

  // Prioridad 4: Meta tag (√öltimo recurso, aqu√≠ suele estar el logo si falla lo dem√°s)
  if (!imagen) {
      imagen = $('meta[property="og:image"]').attr('content');
  }

  // Solo agregamos si hay contenido real
  if (cuerpo.length > 50) {
    results.push({
      json: {
        titulo: titulo,
        content: cuerpo,
        fecha: fecha,
        imagen_url: imagen, 
        autor: autor,
        link: link 
      }
    });
  }
}

return results;

return results;

// Usamos map para procesar cada noticia de la lista
return items.map(item => {
  let rawContent = item.json.content || '';

  // 1. Limpieza de etiquetas HTML
  // Reemplaza <p>, <strong>, etc., por nada
  let cleanText = rawContent.replace(/<[^>]*>?/gm, '');

  // 2. Limpieza de caracteres de escape y espacios
  // Eliminamos \r\n y normalizamos espacios m√∫ltiples
  cleanText = cleanText
    .replace(/\r?\n|\r/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  // Devolvemos el objeto original pero con el content actualizado
  return {
    json: {
      ...item.json,    // Mantiene titulo, link, imagen_url y medio
      content: cleanText
    }
  };
});

return items.map(item => {
  // Aseguramos que content sea un string, si es null o undefined usamos string vac√≠o
  let rawContent = item.json.content || '';

  // Limpieza agresiva de HTML y espacios
  let cleanText = rawContent
    .replace(/<[^>]*>?/gm, ' ') // Quita etiquetas HTML
    .replace(/&nbsp;/g, ' ')    // Quita espacios HTML
    .replace(/&quot;/g, '"')    // Arregla comillas
    .replace(/\r?\n|\r/g, ' ')  // Quita saltos de l√≠nea
    .replace(/\s+/g, ' ')       // Colapsa espacios m√∫ltiples
    .trim();

  // Si el contenido qued√≥ vac√≠o (ej: solo era una foto), usamos el t√≠tulo como backup
  if (cleanText.length < 10) {
      cleanText = item.json.titulo || '';
  }

  return {
    json: {
      ...item.json,
      content: cleanText // Sobrescribimos con el texto limpio y seguro para JSON
    }
  };
});
